package StarPrograms;

public class Star3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n=5;
		for(int i=1;i<n;i++){
		for(int j=0;j<n;j++){
		if(i+j>n-1){
		System.out.print("* ");
		}
		else{
		System.out.println(" ");
		}
		System.out.println();
		}

	}

	}}
